import React from "react";

const Blog = () => {
  const posts = [
    {
      title: "How I Built My Personal Portfolio",
      date: "October 2025",
      summary: "A step-by-step look at how I designed and deployed my own site using React and GitHub Pages.",
      link: "#"
    },
    {
      title: "Top 5 Creative Tools for Developers",
      date: "October 2025",
      summary: "Here are some free and paid tools I use to bring color and creativity to every project I work on.",
      link: "#"
    }
  ];

  return (
    <div className="p-8 bg-gradient-to-b from-blue-50 to-pink-100 min-h-screen">
      <h1 className="text-4xl font-bold mb-6 text-center">Blog Posts</h1>
      <div className="grid md:grid-cols-2 gap-6">
        {posts.map((post, index) => (
          <div key={index} className="bg-white shadow-lg rounded-2xl p-6">
            <h2 className="text-2xl font-semibold mb-2">{post.title}</h2>
            <p className="text-gray-600 text-sm mb-2">{post.date}</p>
            <p className="text-gray-700 mb-4">{post.summary}</p>
            <a
              href={post.link}
              className="text-blue-600 hover:text-blue-800 font-medium"
            >
              Read more â†’
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blog;
